let perforatorSlide=document.querySelector(".perforator"),screwSlide=document.querySelector(".screwdriver"),firstRadio=document.querySelector(".custom-radio-one"),secondRadio=document.querySelector(".custom-radio-two"),forward=document.querySelector(".forward-second"),back=document.querySelector(".back-first");perforatorSlide.classList.add("hidden"),firstRadio.addEventListener("click",function(){firstRadio.checked&&(perforatorSlide.classList.add("hidden"),screwSlide.classList.remove("hidden"))}),secondRadio.addEventListener("click",function(){secondRadio.checked&&(perforatorSlide.classList.remove("hidden"),screwSlide.classList.add("hidden"))}),forward.addEventListener("click",function(){perforatorSlide.classList.remove("hidden"),screwSlide.classList.add("hidden")}),back.addEventListener("click",function(){perforatorSlide.classList.add("hidden"),screwSlide.classList.remove("hidden")});
